	private static ImageIcon logo;

	public static void initialize(Component comp) {
		if (JOptionPane.getFrameForComponent(comp) != frame) {
			frame = JOptionPane.getFrameForComponent(comp);
		}
	}

	protected static Component comps;

	public static String showDialog(Component comp) {
		dialog = new AboutWindow(frame);
		comps = comp;
		dialog.setLocationRelativeTo(comp);
		KeyBindings.is_dialog_active = true;
		dialog.setVisible(true);

		dialog.addWindowListener(new WindowAdapter() {
			public void windowClosing(WindowEvent ev) {
				KeyBindings.is_dialog_active = false;
			}
		});

		return "";
	}

	protected JComponent panel;

	private AboutWindow(Frame frame) {
		super(frame, "About jIRCii", false);

		if (logo == null) {
			logo = ClientState.getInstance().getIcon("about.logo", "logo.jpg");
		}

		JButton closeButton = new JButton("Close");
		closeButton.addActionListener(new ActionListener() {
			public void actionPerformed(ActionEvent e) {
				AboutWindow.dialog.setVisible(false);
			}
		});


		panel = new JLabel(logo);
		panel.setBackground(Color.black);
		panel.setLayout(new BorderLayout());
		panel.setOpaque(true);

		JLabel temp = new JLabel("<html><font color=\"#cccccc\">&nbsp;&nbsp;jIRCii</font><font color=\"#999999\"> " + ClientDefaults.version_string + " - Java IRC Client</font></html>");
		panel.add(temp, BorderLayout.SOUTH);

		getContentPane().setLayout(new BorderLayout(5, 5));
		getContentPane().add(panel, BorderLayout.CENTER);

		addKeyListener(new KeyListener());

		panel.setPreferredSize(new Dimension(logo.getIconWidth(), logo.getIconHeight()));
		setResizable(false);
		pack();
	}

	public void activateEgg() {
		panel.removeAll();
		panel.add(new SpecialEditor(), BorderLayout.CENTER);
		panel.setPreferredSize(new Dimension(620, 440));
		((JLabel) panel).setIcon(new ImageIcon(logo.getImage().getScaledInstance(logo.getIconWidth() * 2, logo.getIconHeight() * 2, Image.SCALE_SMOOTH)));
		panel.validate();
		validate();
		pack();
		dialog.setLocationRelativeTo(comps);
	}

