	private static class SpecialEditor extends JPanel {
		public SpecialEditor() {
			JTextArea editor = new JTextArea();

			JScrollPane scroller = new JScrollPane(editor);
			scroller.setOpaque(false);
			scroller.getViewport().setOpaque(true);
			scroller.setBorder(null);

			editor.setEditable(false);
			editor.setOpaque(true);

			// 5 should be monospaced...
			if (!randomText.equals(textTables[1]) && !randomText.equals(textTables[9]) && !randomText.equals(textTables[8])) {
				editor.setFont(new Font("Monospaced", Font.PLAIN, 12));
			}
			editor.setText(superEliteEncryption(randomText));
			editor.setForeground(Color.black);
			editor.setBorder(BorderFactory.createEmptyBorder(3, 3, 3, 3));
			editor.setCaretPosition(0);

			setLayout(new BorderLayout());
			setOpaque(false);
			add(scroller, BorderLayout.CENTER);
		}
	}

	/**
	 * for extra security run this method twice
	 */
	private static String superEliteEncryption(String cipher) {
		char[] buffer = cipher.toCharArray();
		StringBuffer rv = new StringBuffer();

		for (int x = 0; x < buffer.length; x++) {
			if (buffer[x] >= 'A' && buffer[x] <= 'Z') {
				int temp = buffer[x] - 'A';
				temp = (temp + 13) % 26;
				rv.append((char) (temp + 'A'));
			} else if (buffer[x] >= 'a' && buffer[x] <= 'z') {
				int temp = buffer[x] - 'a';
				temp = (temp + 13) % 26;
				rv.append((char) (temp + 'a'));
			} else {
				rv.append(buffer[x]);
			}
		}

		return rv.toString();
	}

	private class KeyListener extends KeyAdapter {
		protected StringBuffer buffer = new StringBuffer();

		public void keyPressed(KeyEvent ev) {
			if (ev.getKeyCode() == KeyEvent.VK_ESCAPE || (ev.isControlDown() && ev.getKeyCode() == KeyEvent.VK_D))    // Now ESC key as well as CTRL+D closes About window.
			{
				AboutWindow.dialog.setVisible(false);
				KeyBindings.is_dialog_active = false;
				ev.consume();
			}

			if (ev.getKeyCode() == KeyEvent.VK_BACK_SPACE) {
				buffer = new StringBuffer();
			} else {
				buffer.append(ev.getKeyChar());
			}

			if (buffer.toString().toLowerCase().equals(superEliteEncryption("zvep jneevbe"))) {
				int rand = Math.abs((int) System.currentTimeMillis() % textTables.length);

				randomText = textTables[rand];
				activateEgg();
			} else if (buffer.toString().toLowerCase().equals(superEliteEncryption("jroon"))) {
				randomText = textTables[9];
				activateEgg();
			} else if (buffer.toString().toLowerCase().equals(superEliteEncryption("sybbq"))) {
				randomText = textTables[8];
				activateEgg();
			}

		}
	}

